package p2022_12_30;

public class Oper08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 논리 연산자 : and(&&), or(||), not(!)
		
		boolean b1 = true;
		boolean b2 = false;
		
		System.out.println(!b1);		//false
		System.out.println(!b2);		//true
		System.out.println(!true);		//false
		System.out.println(!false);		//true
		
	}

}
